using customerportalapi.Entities;
using customerportalapi.Repositories.Interfaces;
using Microsoft.Extensions.Configuration;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using MongoDB.Driver;
using Moq;
using System;
using System.Collections.Generic;
using System.Linq.Expressions;
using System.Threading.Tasks;

namespace customerportalapi.Repositories.Test
{
    [TestClass]
    public class UserAccountRepositoryTest
    {
        private IConfigurationRoot _configurations;
        private Mock<IMongoCollectionWrapper<UserAccount>> _userAccounts;

        [TestInitialize]
        public void Setup()
        {
            var builder = new ConfigurationBuilder();
            builder.AddJsonFile("appsettings.json");
            _configurations = builder.Build();

            _userAccounts = new Mock<IMongoCollectionWrapper<UserAccount>>();
            _userAccounts.Setup(x => x.FindOne(It.IsAny<Expression<Func<UserAccount, bool>>>(), It.IsAny<FindOptions>())).Returns(
                new List<UserAccount>
                {
                new UserAccount
                {
                    Id = "fake Id",
                    Username = "username",
                    Profilepicture = "KBDKLHUKAFRHFSAJDGLKJ"
                }});
            _userAccounts.Setup(x => x.InsertOne(It.IsAny<UserAccount>())).Verifiable();
            _userAccounts.Setup(x => x.ReplaceOne(It.IsAny<FilterDefinition<UserAccount>>(), It.IsAny<UserAccount>())).Returns(new Mock<ReplaceOneResult>().Object).Verifiable();
            _userAccounts.Setup(x => x.DeleteOneAsync(It.IsAny<FilterDefinition<UserAccount>>())).Returns(Task.FromResult(new Mock<DeleteResult>().Object)).Verifiable();
        }

        [TestMethod]
        public void AlRecuperarUnUsuarioExistente_NoSeProducenErrores()
        {
            //Arrange
            UserAccount userAccount = new UserAccount(){
                Username = "username"
            };
            UserAccountRepository _userAccountRepository = new UserAccountRepository(_configurations, _userAccounts.Object);
            
            //Act
            userAccount = _userAccountRepository.GetAccount("username");

            //Assert
            Assert.AreEqual("username", userAccount.Username);
        }

        [TestMethod]
        public void AlRecuperarUnUsuarioInexistente_NoSeProducenErrores()
        {
            //Arrange
            User user = new User();
            Mock<IMongoCollectionWrapper<User>> _usersInvalid = new Mock<IMongoCollectionWrapper<User>>();
            _usersInvalid.Setup(x => x.FindOne(It.IsAny<Expression<Func<User, bool>>>(), It.IsAny<FindOptions>())).Returns(
                new List<User>());

            UserRepository _userRepository = new UserRepository(_configurations, _usersInvalid.Object);

            //Act
            user = _userRepository.GetCurrentUserByUsername("00000000Z");

            //Assert
            Assert.IsNull(user.Id);
        }

        [TestMethod]
        public void AlActualizarUnUsuario_NoSeProducenErrores()
        {
            //Arrange
            UserAccountRepository _userAccountRepository = new UserAccountRepository(_configurations, _userAccounts.Object);
            UserAccount testUser =_userAccountRepository.GetAccount("12345678Z");
            
            //Act
            testUser.Profilepicture = "newpicture";
            UserAccount modifiedUser = _userAccountRepository.Update(testUser);

            //Assert
            Assert.AreEqual("newpicture", modifiedUser.Profilepicture);
        }

        [TestMethod]
        public void AlEliminarUnUsuario_NoSeProducenErrores()
        {
            UserAccount testUser = GetTestUser();

            UserAccountRepository _userAccountRepository = new UserAccountRepository(_configurations, _userAccounts.Object);
            _userAccountRepository.Delete(testUser);
        }

       

       
        private static UserAccount GetTestUser()
        {
            UserAccount testUser = new UserAccount
            {
                Username = "username",
                Profilepicture =
                    ""
            };
            return testUser;
        }
    }
}
